    new DocumentWidget();
    new DocumentUploader();
    new ChildrenNodesField();
    new StackNodeTree();
    new SaveButtons();
    new TagAutocomplete();
    new NodeTypeFieldsPosition();


    // Init markdown-preview
    _this.$textAreaHTMLeditor = $('textarea[data-uk-htmleditor]');

